var ENV = current.variables.ENV;
var JenkinsUrl = "http://jenkins-awskub.demos.hclets.com/job/";
var job1 = "PCF-Items-API-Pipeline/";
var request = new sn_ws.RESTMessageV2();
var user = 'devopsteam';
var password = 'Devopsteam@123';



	var url = JenkinsUrl +job1 + "build?token=devopscoe";
	gs.info();
	request.setEndpoint(url);
	request.setHttpMethod("post");
	//Endpoint for running the job
	request.setHttpMethod("post");
	request.setBasicAuth(user,password);
	request.setRequestHeader("Accept","application/json");
	var response = request.execute();
	var response2 = response.getStatusCode();
	gs.addInfoMessage("Jenkins PCF Deployment" + response.getBody());
	gs.addInfoMessage("response code: " + response2);

